import { Box, Button, getButtonBaseUtilityClass, Typography } from '@mui/material'
import React, { useState } from 'react'
import "./OrderScreenCss.css"
import ArrowDropUpIcon from '@mui/icons-material/ArrowDropUp';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import { countHandler } from '../../../services/redux/Reducers';
import { useDispatch } from 'react-redux';

function OrderButton() {
    const buttonGroup = {
        "display":"flex"
    }
    const [quantity, setQuantity] = useState(1)
    const [price, setPrice] = useState(299.00)
    const dispatch = useDispatch()
    const handleIncrement = () =>{
        if(quantity<=2){
            dispatch(countHandler(2))
            setQuantity(quantity=>{
                return(quantity+1)
            })
            setPrice(quantity*299.00)
        }
    }
    const handleDecrement = () =>{
        if(quantity>=2){
            setQuantity(quantity=>{
                return(quantity-1)})
            setPrice(quantity*299.00)
        }
    }
  return (
    <Box style={{"width":"100%"}}>
        <Box style={buttonGroup}>
            <p>
                About Product
            </p>
            <Typography
            id='price'
            variant="p"
            noWrap
            component="p"
            sx={{
            //   mr: 2,
              display: { xs: 'flex'},
              flexGrow: 1,
              color: 'inherit',
              textDecoration: 'none',
            }}
            >
                Price <span style={{"marginLeft":"8px","fontWeight":"500"}}>&#36;{price}</span>
            </Typography>
            <div id='buttongroup'>
                <p id='counter'>{quantity}</p>
                <div id="upDownButton">
                <div>
                    <button 
                    className='buttonUpDown'
                    onClick={()=>{
                        handleIncrement()
                    }}
                    >
                        <ArrowDropUpIcon sx={{fontSize:'small'}}/>
                    </button>
                    </div>

                    <div>
                    <button 
                    className='buttonUpDown'
                    onClick={()=>{
                        handleDecrement()
                    }}
                    >
                        <ArrowDropDownIcon sx={{fontSize:'small'}}/>
                    </button>
                </div>               
                </div>
            </div>
            <Button 
            id='BuyNowButon'
            sx={{
                "lineHeight":"none",
        "letterSpacing":"none",
        "textTransform":"capitalize",
        "borderRadius":"25px",
        "paddingLeft":"36px",
        "paddingRight":"36px",
        "fontSize":"medium"
        }}
            variant='contained'>
                Buy Now
            </Button>
        </Box>
    </Box>
  )
}

export default OrderButton
