import { Box,Typography } from '@mui/material'
import React, { useState, useEffect } from 'react'
import "./Product.css"
import { detailData } from '../../../services/redux/State'
import MessageModel from "../Model/MessageModel"

function Product() {
  const [modelMessage, setModelMessage] = useState("")
  const [open, setOpen] = useState(false)
  useEffect(() => {
    let urlMessage = new URLSearchParams(window.location.search)
    console.log(urlMessage.get("success"))
    console.log(urlMessage.get("cancel"))
    if(urlMessage.get("success")){
      setModelMessage("success")
      setOpen(true)
    }
    if(urlMessage.get("cancel")){
      setModelMessage("cancel")
      setOpen(false)
    }
  }, [])

  return (
    <Box className='productDiv' style={{'display':'flex','justifyContent':'center'}}>
        <Box className='productDetail'>
        <Typography
            variant="p"
            sx={{marginTop:"15px",marginBottom:"15px"}}
            component="p"
          >
           Apple Product
          </Typography>
          <Typography
            variant="p"
            component="p"
            sx={{fontSize:"25px",fontWeight:"500"}}
          >
           New Apple AirPods (3D Generation)
          </Typography>
          <Typography
            variant="p"
            className='li'
            component="p"
            sx={{marginTop:"15px",marginBottom:"15px"}}
          >
           It's magic, remasterd!
          </Typography>
          <Typography
            variant="p"
            component="ul"
            sx={{margin:"10px 0 10px"}}
          >
          {detailData.map(elem=>{
            return (
              <Typography
              variant="p"
              component="li"
              className='li'
              sx={{margin:"10px 0 10px"}}
            >
              {elem}
            </Typography>
            )
          })}
          </Typography>
        </Box>
        <Box style={{"width":"43%"}}>
            <div className='imageslider'>
            </div>
            <div style={{"textAlign":"center"}}>
              <span className="dot"></span> 
              <span className="dot"></span> 
              <span className="dot"></span> 
            </div>
        </Box>
        <MessageModel modelMessage={modelMessage} open={open} setOpen={setOpen}/>
    </Box>
  )
}

export default Product
