import { createSlice } from "@reduxjs/toolkit";
import { initialState } from "./State";
import * as action from "./Actions"

const productReducer = createSlice({
    name:"productReducer",
    initialState:initialState,
    reducers:{
        countIncrementHandler:(state,payload1)=>{
            if(state.count>=3){
                state.count = 3
            }
        }
    },
    extraReducers:(builder)=>{
        builder.addCase(action.Get_Product.fulfilled,(state,payload)=>{
            state.product = payload.payload
            state.productFetch = true
        })
        builder.addCase(action.Get_Product.rejected,(state)=>{
            state.error = true
        })
    }
})

export default productReducer.reducer
export const {countIncrementHandler} = productReducer.actions
