import { Box, Typography } from '@mui/material'
import React, { useState } from 'react'

function OrderButton() {
    const [quantity, setQuantity] = useState(1)

  return (
    <Box>
        <Box>
            <Typography
            variant="p"
            noWrap
            component="a"
            href=""
            sx={{
              mr: 2,
              display: { xs: 'flex'},
              flexGrow: 1,
              color: 'inherit',
              textDecoration: 'none',
            }}
            >
                Price <span style={{"marginLeft":"8px","fontWeight":"500"}}>&#36;299.00</span>
            </Typography>
            <input
            value={1} 
            onChange={(e)=>{
                if(quantity>=3){
                    setQuantity(3)
                }
                else if(quantity<=1){
                    setQuantity(1)
                }
                else{
                    setQuantity(e.target.value)
                }
            }}
            type="number"/>
        </Box>
    </Box>
  )
}

export default OrderButton
