import { Box, Button, getButtonBaseUtilityClass, Typography } from '@mui/material'
import React, { useState } from 'react'
import "./OrderScreenCss.css"
import ArrowDropUpIcon from '@mui/icons-material/ArrowDropUp';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import { countIncrementHandler, countDecrementHandler } from '../../../services/redux/Reducers';
import { useDispatch, useSelector } from 'react-redux';
import { Product_Payment } from '../../../services/redux/Actions';
import axios from 'axios';
import { PAYMENT_URL } from '../../../services/Urls';

function OrderButton() {
    const price = useSelector(state => state.productReducer.price)
    console.log(price)
    const count = useSelector(state => state.productReducer.count)
    const buttonGroup = {
        "display": "flex"
    }
    const dispatch = useDispatch()
    const handleIncrement = () => {
        dispatch(countIncrementHandler())
    }
    const handleDecrement = () => {
        dispatch(countDecrementHandler())
    }
    console.log(PAYMENT_URL)
    const orderHandler = ()=>{
        // let data = {
        //     "pk":1,
        //     "price":price,
        //     "quantity":count
        // }
        // dispatch(Product_Payment(data))
        const response = async()=>{
           const data =  await axios({
            method:'post',
            url:`${PAYMENT_URL}1`,
            data:{
                "price":price,
                "count":count
            }
        }).catch(err=>console.log(err))
        return data.data
    }
    return response
}
    return (
        <Box style={{ "width": "100%" }}>
            <Box style={buttonGroup}>
                <p>
                    About Product
                </p>
                <Typography
                    id='price'
                    variant="p"
                    noWrap
                    component="p"
                    sx={{
                        //   mr: 2,
                        display: { xs: 'flex' },
                        flexGrow: 1,
                        color: 'inherit',
                        textDecoration: 'none',
                    }}
                >
                    Price <span style={{ "marginLeft": "8px", "fontWeight": "500" }}>&#36;{price}</span>
                </Typography>
                <div id='buttongroup'>
                    <p id='counter'>{count}</p>
                    <div id="upDownButton">
                        <div>
                            <button
                                className='buttonUpDown'
                                onClick={() => {
                                    handleIncrement()
                                }}
                            >
                                <ArrowDropUpIcon sx={{ fontSize: 'small' }} />
                            </button>
                        </div>

                        <div>
                            <button
                                className='buttonUpDown'
                                onClick={() => {
                                    handleDecrement()
                                }}
                            >
                                <ArrowDropDownIcon sx={{ fontSize: 'small' }} />
                            </button>
                        </div>
                    </div>
                </div>
                <form action={`${PAYMENT_URL}1`} method='POST'>
                <Button
                type='submit'
                    id='BuyNowButon'
                    sx={{
                        "lineHeight": "none",
                        "letterSpacing": "none",
                        "textTransform": "capitalize",
                        "borderRadius": "25px",
                        "paddingLeft": "36px",
                        "paddingRight": "36px",
                        "fontSize": "medium"
                    }}
                    variant='contained'>
                    Buy Now
                </Button>
                </form>
            </Box>
        </Box>
    )
}

export default OrderButton
