import { Box,Typography } from '@mui/material'
import React, { useState, useEffect } from 'react'
import "./Product.css"
import { detailData } from '../../../services/redux/State'
import MessageModel from "../Model/MessageModel"

function Product() {
  const [modelMessage, setModelMessage] = useState("")
  useEffect(() => {
    let urlMessage = new URLSearchParams(window.location.search)
    if(urlMessage.get("success")){
      setModelMessage("success")
    }
    if(urlMessage.get("cancel")){
      setModelMessage("cancel")
    }
  }, [])

  return (
    <Box style={{'display':'flex','justifyContent':'center'}}>
        <Box className='productDetail'>
        <Typography
            variant="p"
            sx={{marginTop:"15px",marginBottom:"15px"}}
            component="p"
          >
           Apple Product
          </Typography>
          <Typography
            variant="p"
            component="p"
            sx={{fontSize:"25px",fontWeight:"500"}}
          >
           New Apple AirPods (3D Generation)
          </Typography>
          <Typography
            variant="p"
            className='li'
            component="p"
            sx={{marginTop:"15px",marginBottom:"15px"}}
          >
           It's magic, remasterd!
          </Typography>
          <Typography
            variant="p"
            component="ul"
            sx={{margin:"10px 0 10px"}}
          >
          {detailData.map(elem=>{
            return (
              <Typography
              variant="p"
              component="li"
              className='li'
              sx={{margin:"10px 0 10px"}}
            >
              {elem}
            </Typography>
            )
          })}
          </Typography>
        </Box>
        <Box style={{"width":"43%"}}>
            <div className='imageslider'>
            </div>
            <div style={{"text-align":"center"}}>
              <span className="dot"></span> 
              <span className="dot"></span> 
              <span className="dot"></span> 
            </div>
        </Box>
        <MessageModel modelMessage={modelMessage}/>
    </Box>
  )
}

export default Product
